import "./App.css";
import React, { useEffect, useState } from "react";
import axios from "axios";

function App() {
  const [value, setValue] = useState("");

  const handleSubmit = async () => {
    const { data } = await axios.post("http://localhost:8080/form", {
      value,
      name: "A",
      age: 44,
    });
    console.log(data);
  };

  useEffect(() => {
    const getEvents = async () => {
      // if(sessionStorage.getItem('events')) {
      //   const data =sessionStorage.getItem('events')
      // }else {
      //   const { data } = await axios.get("http://localhost:8080/events");
      // }
      const eventsFromStorage = sessionStorage.getItem("events");

      const data = eventsFromStorage
        ? eventsFromStorage
        : await axios.get("http://localhost:8080/events");
      console.log(data);
      sessionStorage.setItem("events", JSON.stringify(data));
    };
    getEvents();
  }, []);

  return (
    <div className="App">
      <h1>React-App</h1>
      <input
        type="text"
        value={value}
        onChange={(e) => setValue(e.target.value)}
      />
      <button onClick={handleSubmit}>submit</button>
    </div>
  );
}

export default App;
