import "./App.css";
import React, { useEffect, useState } from "react";
import axios from "axios";
import { Bar, line, Doughnut } from "react-chartjs-2";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
} from "chart.js";

function App() {
  const [value, setValue] = useState("");
  const [events, setEvents] = useState([]);

  const handleSubmit = async () => {
    const { data } = await axios.post("http://localhost:8080/form", {
      value,
      name: "A",
      age: 44,
    });
    console.log(data);
  };
  const barData = {
    labels: ["Fall", "Winter", "Spring", "Summer"],
    datasets: {
      label: "thisYear",
      type: "bar",
      backgroundColor: "grey",
      data: [20, 10, 25, 30],
    },
  };

  const barOptions = {};

  useEffect(() => {
    const getEvents = async () => {
      if (sessionStorage.getItem("events")) {
        console.log("if");
        setEvents(JSON.parse(sessionStorage.getItem("events")));
        console.log(events);
      } else {
        console.log("else");
        const { data } = await axios.get("http://localhost:8080/events");
        console.log(data);
        sessionStorage.setItem("events", JSON.stringify(data));
      }
    };
    getEvents();
  }, []);

  return (
    <div className="App">
      <h1>React-App</h1>
      <input
        type="text"
        value={value}
        onChange={(e) => setValue(e.target.value)}
      />
      <button onClick={handleSubmit}>submit</button>
      <div className="bar">
        <Bar data={barData} options={barOptions} />
      </div>
    </div>
  );
}

export default App;
