import "./App.css";
import React, { useEffect, useState } from "react";
import axios from "axios";
import HelpIcon from "@material-ui/icons/Help";

function App() {
  const [value, setValue] = useState("");
  const [events, setEvents] = useState([]);

  const handleSubmit = async () => {
    const { data } = await axios.post("http://localhost:8080/form", {
      value,
      name: "A",
      age: 44,
    });
    console.log(data);
  };

  useEffect(() => {
    const getEvents = async () => {
      if (sessionStorage.getItem("events")) {
        console.log("if");
        setEvents(JSON.parse(sessionStorage.getItem("events")));
        console.log(events);
      } else {
        console.log("else");
        const { data } = await axios.get("http://localhost:8080/events");
        console.log(data);
        sessionStorage.setItem("events", JSON.stringify(data));
      }
    };
    getEvents();
  }, []);

  return (
    <div className="App">
      <h1>React-App</h1>
      <input
        type="text"
        value={value}
        onChange={(e) => setValue(e.target.value)}
      />
      <button onClick={handleSubmit}>submit</button>
      <MarkEmailReadIcon />
    </div>
  );
}

export default App;
