import "./App.css";
import React, { useEffect, useState } from "react";
import axios from "axios";
import MarkEmailReadIcon from "@mui/icons-material/MarkEmailRead";
import ArrowCircleLeftIcon from "@mui/icons-material/ArrowCircleLeft";
import { Chart as ChartJS, ArcElement, Tooltip, Legend, Title } from "chart.js";
import { Pie, Bar } from "react-chartjs-2";
ChartJS.register(ArcElement, Tooltip, Legend, Title);

function App() {
  const [value, setValue] = useState("");
  const [events, setEvents] = useState([]);
  const [taskName, setTaskName] = useState("");
  const [randSet,setRandSet]= useState([])

  const handleSubmit = async () => {
    const { data } = await axios.post("http://localhost:8080/form", {
      value,
      name: "A",
      age: 44,
    });
    console.log(data);
  };
  const handleSubmit2 = async () => {
    const { data } = await axios.post("http://localhost:8080/event/" + value);
    setTaskName(data);
    console.log(data);
  };

  const handlePieData = async () => {
    const { data } = await axios.get("http://localhost:8080/pie")
    setRandSet(data)
    console.log(data);
  }
  const pieData = {
    labels: ["a", "b", "c", "d", "e", "f"],
    datasets: [
      {
        label: "# of Votes",
        data: [1, 20, 304, 54, 230, 450, 2405],
        backgroundColor: [
          "rgba(255, 99, 132, 0.2)",
          "rgba(54, 162, 235, 0.2)",
          "rgba(255, 206, 86, 0.2)",
          "rgba(75, 192, 192, 0.2)",
          "rgba(153, 102, 255, 0.2)",
          "rgba(255, 159, 64, 0.2)",
          "rgba(255, 109, 64, 0.6)",
          "rgba(125, 169, 34, 0.8)",
          "rgba(225, 99, 251, 0.3)",
          "rgba(225, 99, 101, 0.4)",
        ],
        borderColor: [
          "rgba(255, 99, 132, 1)",
          "rgba(54, 162, 235, 1)",
          "rgba(255, 206, 86, 1)",
          "rgba(75, 192, 192, 1)",
          "rgba(153, 102, 255, 1)",
          "rgba(255, 159, 64, 1)",
          "rgba(255, 109, 64, 0.6)",
          "rgba(125, 169, 34, 0.8)",
          "rgba(225, 99, 251, 0.3)",
          "rgba(225, 99, 101, 0.4)",
        ],
        // borderWidth: 1,
        //hoverOffset:20
        // offset: [20, 0, 0, 0, 0, 0, 0, 0, 0, 0],
      },
    ],
  };

  useEffect(() => {
    const getEvents = async () => {
      if (sessionStorage.getItem("events")) {
        console.log("if");
        setEvents(JSON.parse(sessionStorage.getItem("events")));
        console.log(events);
      } else {
        console.log("else");
        const { data } = await axios.get("http://localhost:8080/events");
        console.log(data);
        sessionStorage.setItem("events", JSON.stringify(data));
      }
    };
    getEvents();
  }, []);

  return (
    <div className="App">
      <h1>React-App</h1>
      <input
        type="text"
        value={value}
        onChange={(e) => setValue(e.target.value)}
      />
      <button onClick={handleSubmit}>submit</button>
      <button onClick={handleSubmit2}>search event</button>
      <p>{taskName}</p>
      <MarkEmailReadIcon />
      <ArrowCircleLeftIcon />
      <div style={{ width: "20rem" }}>
        {/* <Pie data={pieData} /> */}
        {/* <Bar data={barData} /> */}
      </div>
      <button onClick={handlePieData}>generate data</button>
    </div>
  );
}

export default App;
